function Button({color, fontColor, size='md', text}) {

    const btnSize = size === 'md' ? '5px' : '50px'

    return (
        <div>
            <button 
                style={{
                    backgroundColor: `${color}`, 
                    color: `${fontColor}`,
                    borderRadius: '5px',
                    padding: `5px ${btnSize}`
                }}
            >
                Click me
            </button>
        </div>
    )
}

export default Button
